noscript.script:
window.jQuery(function ($) {
	'use strict';

	var types = {
		'Informação': [
			'Cidades e CEPs atendidos',
			'Informaçao de Agência',
			'Objetos Transportados',
			'Restrições de peso e dimensões da encomenda',
			'Prazo de entrega',
			'Processo de envio / retirada',
			'Produtos promocionais',
			'Contato do Comercial',
			'Outra (Tema Inexiste)'
		],
		'Reclamação': [
			'Atendimento Agência',
			'Atendimento SAC',
			'Desvio da mercadoria',
			'Prazo de entrega',
			'Preço',
			'Site',
			'Outra (Tema Inexiste)'
		],
		'Coleta': null,
		'Documentos/relatórios personalizados': [
			'Xml',
			'Comprovantes de entrega',
			'Relatório personalizado'
        ],
        'Lei Geral de Proteção de Dados': [
			'Relatório de dados pessoais (LGPD)',
			'Cancelar recebimento de comunicações'
		],
		'Elogio': [
			'A Colaboradores',
			'Outra (Tema Inexiste)'
		],
		'Faturamento / Cobrança': null,
		'Rastreamento': null,
		'Sugestão': null,
	}

	var $form = $('#form-sf');
	var $reasons = $form.find('#00N1N00000FA8oC');
	var $person_type = $('.person-type input');
	var $cpf = $('#cpf');
	var $cnpj = $('#cnpj');
	var $reasons_opts = $reasons.find('option');

	hideReason();

	$form.find('#type').on('change', function (e) {
		var value = $(this).val();
		var reasons = types[value];

		$reasons.val('');

		if ( null != reasons ) {
			$reasons_opts.hide();
			$reasons_opts.each(function () {
				var $opt = $(this)
				var value = $opt.attr('value');

				if ('' == value || reasons.indexOf(value) != -1) {
					$opt.show();
				}
			})
			showReason();
		} else {
			hideReason();
		}
	})

	$cnpj.parent().hide();
	$person_type.parents('.row').show();

	$person_type.on('change', function () {
		var person_type = getPersonType();

		$cpf.val('');
		$cnpj.val('');

		if ( 1 == person_type ) {
			$cpf.parent().show();
			$cnpj.parent().hide();
			$cpf.prop('required', true);
			$cnpj.prop('required', false);
		} else {
			$cpf.parent().hide();
			$cnpj.parent().show();
			$cpf.prop('required', false);
			$cnpj.prop('required', true);
		}
	})

	$('#cpf, #cnpj').on('input', function () {
		this.value = this.value.replace(/[^0-9]/g, '');
	})

	$form.on('submit', function (e) {
		var error = true;
		var person_type = getPersonType();
		var number = 1 == person_type ? $('#cpf').val() : $('#cnpj').val()

		if ( 1 == person_type ) {
			error = !validateCPF(number);
		} else {
			error = !validateCNPJ(number);
		}

       	//e.preventDefault();

		if (error) {
			alert('Seu ' + (1 == person_type ? 'CPF' : 'CNPJ') + ' está incorreto.')
			return false;
		} else {
        	$form.find('[type="submit"]')
              	.prop('disabled', true)
          		.text('Enviando')

			/*	
          var url = $form.attr('action');
          	var req = $.ajax(url, {
              method: $form.attr('method'),
              data: $form.serialize(),
            })
            req.done(function () {
            	console.log('success', arguments);
            })
          	req.fail(function () {
              	console.log('error', arguments);
            })
            */
        }
	});

	function showReason () {
		$reasons.parent().fadeIn(250)
		$reasons.attr('required', true)
	}

	function hideReason () {
		$reasons.parent().fadeOut(250)
		$reasons.attr('required', false)
	}

	function getPersonType () {
		return $person_type.filter(':checked').val();
	}

	// validateCPF (string) : boolean
	// validateCNPJ (string) : boolean
	function calc_digitos_posicoes(i){var t=1<arguments.length&&void 0!==arguments[1]?arguments[1]:10,o=2<arguments.length&&void 0!==arguments[2]?arguments[2]:0;i=i.toString();for(var c=0;c<i.length;c++)o+=i[c]*t,--t<2&&(t=9);return i+(o=(o%=11)<2?0:11-o)}function validateCPF(i){return calc_digitos_posicoes(calc_digitos_posicoes((i=(i=i.toString()).replace(/[^0-9]/g,"")).substr(0,9)),11)===i}function validateCNPJ(i){var t=i=(i=i.toString()).replace(/[^0-9]/g,"");return calc_digitos_posicoes(calc_digitos_posicoes(i.substr(0,12),5),6)===t}
});
